import '../App/App.css'
import React, { Component } from 'react'
import Searchbar from '../Searchbar/Searchbar'
import { fetchImages } from '../../services/fetchImages'
import ImageGallery from '../ImageGallery/ImageGallery'
import ImageGalleryItem from '../ImageGalleryItem/ImageGalleryItem'
import Button from '../Button/Button'
import scrollTo from '../../services/scrollTo.js'
import Spinner from '../Loader/Loader'
// import Modal from '../Modal/Modal'

export default class App extends Component {
  state = {
    searchQuery: '',
    queryStatus: 'idle',
    images: [],
    currentPage: null,
    selectedImage: '',
  }

  handleSubmit = (searchQuery) => {
    this.setState({ searchQuery, currentPage: 1 })
  }

  async componentDidUpdate(_, prevState) {
    const { searchQuery, currentPage } = this.state

    const shouldFetch =
      (prevState.searchQuery !== searchQuery && searchQuery !== '') ||
      prevState.currentPage !== currentPage

    if (shouldFetch) {
      try {
        this.setState({ queryStatus: 'pending' })

        const images = await fetchImages(searchQuery, currentPage)

        this.setState((prevState) => ({
          queryStatus: 'resolved',
          images: [...prevState.images, ...images],
        }))

        scrollTo()
      } catch (error) {
        this.setState({ reqStatus: 'rejected' })
      }
    }
  }

  handleMoreBtnClick = (e) => {
    e.preventDefault()

    this.setState((prevState) => ({
      currentPage: prevState.currentPage + 1,
    }))
  }

  handleImgClick = (e) => {
    const largeImageURL = e.target
    console.log(largeImageURL)
  }

  render() {
    const { images, queryStatus } = this.state

    return (
      <>
        <Searchbar onSubmit={this.handleSubmit} />

        <ImageGallery>
          {images.map(({ id, webformatURL, tags }) => (
            <ImageGalleryItem
              key={id}
              webformatURL={webformatURL}
              tags={tags}
              onImgClick={this.handleImgClick}
            />
          ))}
        </ImageGallery>

        {queryStatus === 'resolved' && (
          <Button handleMoreBtnClick={this.handleMoreBtnClick} />
        )}

        {queryStatus === 'pending' && <Spinner />}

        {/* <Modal></Modal> */}
      </>
    )
  }
}
